$(document).ready(function(){
var winwidth = $( window ).width(); 
    var offset;
    //============== nawigacja colour
    $(window).scroll(function(){
        offset = $(window).scrollTop();
        
        if(offset > 85 ) {
            $(".nav").css("background-color","  rgba(125, 167, 209, 0.93)");
            $(".nav").css("box-shadow","1px 0px 4px #7da7d1");
            $(".list2 li").css("border","0.4px solid #fff");
            $(".container-hamburger").css("border","0.4px solid #fff");
            if(winwidth > 900){
                $(".nav-list").css("margin-top","-20px");
            }
        }
        if(offset <= 85 ) {
            $(".nav").css("background-color","transparent");
            $(".nav").css("box-shadow","0px 0px 0px white");
            $(".list2 li").css("border","0.4px solid #aaa");
            $(".container-hamburger").css("border","0.4px solid #aaa");
            if(winwidth > 900){
                $(".nav-list").css("margin-top","0px");
            }
        }
    });
    
    //============ language
    $(".en").click(function(){
        $(this).css("display","none");
        $(".pl").css("display","block");
    });
    $(".pl").click(function(){
        $(this).css("display","none");
        $(".en").css("display","block");
    });
    //======nawigacja scroll to=======
    $(".li-home").click(function(){
//        $(".nav-mobile").css("display","none");
        $("html, body").animate({
            scrollTop: 0
        },250,"linear");
    });
    $(".li-about").click(function(){
        $("html, body").animate({
            scrollTop: $(".about-container").offset().top -30
        },250,"linear");
    });
    $(".li-skills").click(function(){
        $("html, body").animate({
            scrollTop: $(".skills").offset().top -150
        },250,"linear");
    });
    $(".li-portfolio").click(function(){
        $("html, body").animate({
            scrollTop: $(".portfolio").offset().top -180
        },250,"linear");
    });
    $(".li-hobby").click(function(){
        $("html, body").animate({
            scrollTop: $(".hobby").offset().top -150
        },250,"linear");
    });
    $(".li-contact").click(function(){
        $("html, body").animate({
            scrollTop: $(".contact").offset().top -150
        },250,"linear");
    });
    //=====mobile
    $(".list-mobile li:first-child").click(function(){
        $(".nav-mobile").css("display","none");
        $(".bar1").css({
            '-webkit-transform': 'rotate(0deg) translate(0px, 0px)',
            'transform': 'rotate(0deg) translate(0px, 0px)'
        });
        $(".bar2").css({
            'opacity': '1'
        });
        $(".bar3").css({
            '-webkit-transform': 'rotate(0deg) translate(0px, 0px)',
            'transform': 'rotate(0deg) translate(0px, 0px)'
        });
        $("html, body").animate({
            scrollTop: 0
        },250,"linear");
    });
    $(".list-mobile li:nth-child(2)").click(function(){
        $(".nav-mobile").css("display","none");
        $("html, body").animate({
            scrollTop: $(".about-container").offset().top -30
        },250,"linear");
    });
    $(".list-mobile li:nth-child(3)").click(function(){
        $(".nav-mobile").css("display","none");
        $("html, body").animate({
            scrollTop: $(".skills").offset().top -150
        },250,"linear");
    });
    $(".list-mobile li:nth-child(4)").click(function(){
        $(".nav-mobile").css("display","none");
        $("html, body").animate({
            scrollTop: $(".portfolio").offset().top -180
        },250,"linear");
    });
    $(".list-mobile li:nth-child(5)").click(function(){
        $(".nav-mobile").css("display","none");
        $("html, body").animate({
            scrollTop: $(".hobby").offset().top -150
        },250,"linear");
    });
    $(".list-mobile li:nth-child(6)").click(function(){
        $(".nav-mobile").css("display","none");
        $("html, body").animate({
            scrollTop: $(".contact").offset().top -150
        },250,"linear");
    });
    //=========== hambuger
    $(".container-hamburger").click(function(){
        if($(".nav-mobile").css("display")=="none"){
            $(".nav-mobile").css("display","block");
            $(".list1").css("visibility","hidden");
            $(".nav").css("background","transparent");
            $(".nav").css("box-shadow","none");
            $(".container-hamburger").css("border","0.4px solid #fff");
        }
        else{
            $(".nav-mobile").css("display","none");
            $(".list1").css("visibility","visible");
            $(".container-hamburger").css("border","0.4px solid #fff");
            if(offset > 85){
                $(".nav").css("background","  rgba(125, 167, 209, 0.93)");
                $(".nav").css("box-shadow","box-shadow","1px 0px 4px #7da7d1");
            }
        }
    });
    //=========== formularz
    $( window ).scroll(function(){
        if ($('.name:text').val().length == 0) {
            $(".name-label").removeClass("clicked-label");
        }
        if ($('.email:text').val().length == 0) {
            $(".email-label").removeClass("clicked-label");
        }
        if ($('.subject:text').val().length == 0) {
            $(".subject-label").removeClass("clicked-label");
        }
        if ($('.message').val().length == 0) {
            $(".message-label").removeClass("clicked-label");
        }
    });
//    $(".name").keypress(function() {
//        if($(this).val().length > 1) {
//            $(".name-label").addClass("clicked-label");
//        } else {
//             // Disable submit button
//        }
//    });
//    $(".name").keypress(function() {
//        if($(this).val().length > 1) {
//            $(".name-label").addClass("clicked-label");
//        } else {
//             // Disable submit button
//        }
//    });
    $(".name").focus(function(){
        $(".name-label").toggleClass("clicked-label");
        if ($('.name:text').val().length != 0) {
            $(".name-label").addClass("clicked-label");
        }
        if ($('.email:text').val().length == 0) {
            $(".email-label").removeClass("clicked-label");
        }
        if ($('.subject:text').val().length == 0) {
            $(".subject-label").removeClass("clicked-label");
        }
        if ($('.message').val().length == 0) {
            $(".message-label").removeClass("clicked-label");
        }
    });
    $(".email").focus(function(){
        $(".email-label").toggleClass("clicked-label");
        if ($('.name:text').val().length == 0) {
            $(".name-label").removeClass("clicked-label");
        }
        if ($('.email:text').val().length != 0) {
            $(".email-label").addClass("clicked-label");
        }
        if ($('.subject:text').val().length == 0) {
            $(".subject-label").removeClass("clicked-label");
        }
        if ($('.message').val().length == 0) {
            $(".message-label").removeClass("clicked-label");
        }
    });
    $(".subject").focus(function(){
        $(".subject-label").toggleClass("clicked-label");
        if ($('.name:text').val().length == 0) {
            $(".name-label").removeClass("clicked-label");
        }
        if ($('.email:text').val().length == 0) {
            $(".email-label").removeClass("clicked-label");
        }
        if ($('.subject:text').val().length != 0) {
            $(".subject-label").addClass("clicked-label");
        }
        if ($('.message').val().length == 0) {
            $(".message-label").removeClass("clicked-label");
        }
    });
    $(".message").focus(function(){
        $(".message-label").toggleClass("clicked-label");
        if ($('.name:text').val().length == 0) {
            $(".name-label").removeClass("clicked-label");
        }
        if ($('.email:text').val().length == 0) {
            $(".email-label").removeClass("clicked-label");
        }
        if ($('.subject:text').val().length == 0) {
            $(".subject-label").removeClass("clicked-label");
        }
        if ($('.message').val().length != 0) {
            $(".message-label").addClass("clicked-label");
        }
    });
    //=========== skills kola
    $( window ).scroll(function(){
        offset = $(window).scrollTop();
        var skills = $(".skills").offset().top;
        var services = $(".services").offset().top;
        var hobby = $(".hobby").offset().top;
        if(offset > skills -500) {
            $(".sq1").animate({
                height: '100px'
            },450,"linear");
            $(".sq2-1").delay( 450 ).animate({
                height: '50px',
                top: '50px'
            },350,"linear");
            $(".sq3").delay( 800 ).animate({
                height: '50px',
                top: '0px'
            },250,"linear");
        }
        if(offset > services -500) {
            $(".service").animate({
                top: '0px',
                opacity:'1'
            },350);
        }
    });
});